import React from 'react';
import './App.css';
import InputBox from '../components/inputBox'
import recipeBox from '../components/RECIPEBOX'

class App extends React.Component {
	  constructor(){
	    super()
	    this.state = {
	      inputfield: '',
	      listOfRecipes: []
	    }
	  }

	searchClicked = () => {
		let ingredient = this.state.inputfield;

		let data = fetch('https://api.spoonacular.com/recipes/findByIngredients?ingredients=' + ingredient + ',+flour,+sugar&apiKey=789031a137c64d8c99ed7d24a72ba08c')
		  .then(response => response.json())
		  .then(console.log)


	  	this.setState({inputfield: data});

