import React from 'react';
import './App.css';
import InputBox from '../components/inputBox'

class App extends React.Component {
	  constructor(){
	    super()
	    this.state = {
	      inputfield: ''
	    }
	  }

	addClicked = () => {
	    recipes = fetch('https://api.spoonacular.com/recipes/findByIngredients?ingredients=apples,+flour,+sugar&number=2')
	    .then(response=>response.json())
	    console.log(recipes)
	  }

	keyPressed = (event) => {
	    if(event.key === 'Enter'){
	      this.addClicked();
	    }
	  }

	render(){
	  return (
	    <div className="App">
	      <header>
	        Welcome to the recipe finder!
	      </header>
	      <InputBox inputValue={this.state.inputfield} searchClicked = {this.searchClicked} onKeyPress={this.keyPressed} changed={this.handleChange}/>
	    </div>
	  );
	}
}

export default App;
