import React from 'react';
import './App.css';
import InputBox from '../components/inputBox'

class App extends React.Component {
	  constructor(){
	    super()
	    this.state = {
	      inputfield: '',
	      listOfRecipes: []
	    }
	  }

	searchClicked = () => {
		let ingredient = this.state.inputfield;

		async function fetchData(ingredient){
		  // const response = await fetch('https://api.spoonacular.com/recipes/findByIngredients?ingredients=' + ingredient + '&apiKey=789031a137c64d8c99ed7d24a72ba08c');
		  const response = await fetch('https://api.spoonacular.com/recipes/findByIngredients?ingredients=apples,+flour,+sugar&number=2');
		  const data = await response.json();
		  console.log(data);
		  // this.setState({inputfield:data.});
		  return(data);
		}

		let response = fetchData(ingredient);
	  }

	keyPressed = (event) => {
	    if(event.key === 'Enter'){
	      this.searchClicked();
	    }
	  }


	render(){
	  return (
	    <div className="App">
	      <header>
	        Welcome to the recipe finder!
	      </header>
	      <InputBox inputValue={this.state.inputfield} searchClicked = {this.searchClicked} onKeyPress={this.keyPressed}/>
	    </div>
	  );
	}
}

export default App;
